let db=[];async function formTaker(e){if(0!=e.length){let t=document.querySelector(".country-list-ul");e.forEach(e=>{let n=`<li data-id="${e.id}" data-code="${e.code.toLowerCase()}" data-phonecode="${e.phone}" class="country-list-point "><span class="country-name">${e.name}</span> <span class="country-code">+${e.phone}</span> <img src="./assets/icons/countries/${e.code.toLowerCase()}.png"></li>`;t.insertAdjacentHTML("beforeend",n)}),changeCountry()}}fetch("https://raw.githubusercontent.com/AleksandrBrown/telPlaginData/main/countries.json").then(e=>e.json()).then(e=>{e.forEach(e=>{db.push(e)}),formTaker(db)});let countryTelCode=document.querySelector(".label-4-tel"),defaultPoint=document.querySelector(".choosen-country-flag-img");function changeCountry(){document.querySelector(".country-list-ul").addEventListener("click",e=>{"LI"!=e.target.tagName?notLiClick(e.target):LiClick(e.target),document.querySelector(".country-list").hidden=!0})}function notLiClick(e){defaultPoint.src=`./assets/icons/countries/${e.parentElement.dataset.code}.png`,countryTelCode.innerHTML="+"+e.parentElement.dataset.phonecode}function LiClick(e){defaultPoint.src=`./assets/icons/countries/${e.dataset.code}.png`,countryTelCode.innerHTML="+"+e.dataset.phonecode}const tabQuestions=document.querySelector(".tab-m-q"),contentArrQuestions=[...document.querySelectorAll(".tab-c-q")];tabQuestions.addEventListener("change",()=>{contentArrQuestions.forEach(e=>{e.classList.contains(""+tabQuestions.value)&&(contentArrQuestions.forEach(e=>e.classList.remove("uk-active")),e.classList.add("uk-active"))})});