const tabQuestions=document.querySelector(".tab-m-q"),contentArrQuestions=[...document.querySelectorAll(".tab-c-q")];tabQuestions.addEventListener("change",()=>{contentArrQuestions.forEach(t=>{t.classList.contains(""+tabQuestions.value)&&(contentArrQuestions.forEach(t=>t.classList.remove("uk-active")),t.classList.add("uk-active"))})});